mapper.select:
SELECT d.id, d.drug_id, d.farm_name, d.grow_week_age, d.drug_name, round(d.use_unit,0) as use_unit, d.use_type from s_b_use_drug_plan d left join s_d_goods g on g.good_id = d.drug_id where 1 = 1
and d.grow_week_age in
#{item}
and g.good_type = #{good_type}
and d.drug_id = #{drug_id}
and d.grow_week_age >= #{start_grow_week_age}
and #{end_grow_week_age} >=d.grow_week_age
order by d.modify_time desc,d.create_time desc

mapper.delete:
	delete from s_b_use_drug 
	where 1=1

        and id = #{id}

mapper.select:
select distinct u.id,u.user_real_name from v_user u where u.obj_id in(SELECT distinct obj_id FROM v_user where obj_type =2 and id = #{user_id})
and u.obj_id = #{houseId}
order by u.obj_id

select f.* from s_d_goods g left join s_d_stock s on s.good_id = g.good_id left join s_d_factory f on f.factory_id = s.factory_id where 1=1
and g.good_type = #{good_type}
and g.good_id = #{good_id}
