mapper.select:
SELECT farm_id, house_id, date, right(time,2) as time, batch_no, ROUND(inside_temp1,1) inside_temp1, ROUND(inside_temp2,1) inside_temp2, ROUND(inside_temp3,1) inside_temp3, ROUND(inside_temp4,1) inside_temp4, ROUND(inside_avg_temp,1) inside_avg_temp, ROUND(inside_set_temp,1) inside_set_temp, ROUND(high_alarm_temp,1) high_alarm_temp, ROUND(low_alarm_temp,1) low_alarm_temp, ROUND(outside_temp,1) outside_temp FROM rpt_monitor_daily where 1=1
and farm_id = #{farmId}
and house_id = #{houseId}
and batch_no = #{batchId}
AND (`date` BETWEEN #{beginTime} AND #{endTime} )
order by date

SELECT farm_id, house_id, year, month, date, batch_no, ROUND(inside_temp1,1) inside_temp1, ROUND(inside_temp2,1) inside_temp2, ROUND(inside_temp3,1) inside_temp3, ROUND(inside_temp4,1) inside_temp4, ROUND(inside_avg_temp,1) inside_avg_temp, ROUND(inside_set_temp,1) inside_set_temp, ROUND(high_alarm_temp,1) high_alarm_temp, ROUND(low_alarm_temp,1) low_alarm_temp, ROUND(outside_temp,1) outside_temp FROM rpt_monitor_month where 1=1
and farm_id = #{farmId}
and house_id = #{houseId}
and batch_no = #{batchId}
AND (`date` BETWEEN #{beginTime} AND #{endTime} )
order by date
