mapper.select:
select d.id, d.organization_id, d.name_cn, d.parent_id, d.level_id, d.level_name, d.create_person, d.create_date, d.create_time from s_d_organization d LEFT JOIN s_d_rights c on d.id = c.obj_id LEFT JOIN s_d_role_rights a on c.rights_id = a.rights_id LEFT JOIN s_d_user_role b on a.role_id = b.role_id where c.obj_type = 2
and b.user_id = #{user_id}
and d.parent_id = #{parent_id}
and a.role_id = #{role_id}
order by d.id

select d.id, d.organization_id, d.name_cn, d.parent_id, d.level_id, d.level_name, d.create_person, d.create_date, d.create_time from s_d_organization d LEFT JOIN s_d_rights c on d.id = c.obj_id LEFT JOIN s_d_role_rights a on c.rights_id = a.rights_id where c.obj_type = 2
and a.role_id = #{role_id}
order by d.id

SELECT org.id, org.organization_id, org.name_cn, org.parent_id, org.level_id, org.level_name, org.create_person, org.create_date, org.create_time, farm.farm_add1, farm.farm_add2, farm.farm_add3 FROM s_d_organization org LEFT JOIN s_d_farm farm ON farm.id = org.id where 1=1
and org.id = #{id}
and org.organization_id = #{organization_id}
order by org.id
