mapper.select:
select d.id, d.organization_id, d.name_cn, d.parent_id, d.level_id, d.level_name, d.create_person, d.create_date, d.create_time, devi.device_code from s_d_organization d LEFT JOIN s_d_rights c on d.id = c.obj_id LEFT JOIN s_d_role_rights a on c.rights_id = a.rights_id LEFT JOIN s_d_user_role b on a.role_id = b.role_id LEFT JOIN s_b_devi_house devi on devi.house_id = d.id and devi.farm_id = d.parent_id where c.obj_type = 2 AND d.freeze_status=0
and b.user_id = #{user_id}
and d.parent_id = #{parent_id}
and a.role_id = #{role_id}
order by d.id

SELECT a.id, a.organization_id, a.name_cn, a.parent_id, b.name_cn parent_name, a.level_id, a.level_name, a.create_person, a.create_date, a.create_time, farm.farm_add1, farm.farm_add2, farm.farm_add3, house.house_type, code.code_name house_type_name FROM s_d_organization a LEFT JOIN s_d_organization b ON a.parent_id = b.id LEFT JOIN s_d_farm farm ON farm.id = a.id LEFT JOIN s_d_house house ON house.id = a.id LEFT JOIN (SELECT * FROM s_d_code WHERE code_type='HOUSE_TYPE') code ON code.biz_code = house.house_type WHERE 1=1 AND a.freeze_status=0
AND a.id = #{id}
AND a.organization_id = #{organization_id}
AND a.parent_id = #{parent_id}
AND a.parent_id IN #{item}
AND a.level_id = #{level_id}
ORDER BY id

select u.id user_id ,u.user_code ,u.user_real_name_en ,u.user_real_name ,ur.role_id ,rr.rights_id ,rr.write_read ,org.id org_id ,org.organization_id org_code ,org.name_cn org_name ,org.parent_id org_parent_id ,org.level_id org_level_id from s_d_user u left join s_d_user_role ur on u.id = ur.user_id left join s_d_role_rights rr on ur.role_id = rr.role_id left join s_d_rights r on rr.rights_id = r.rights_id left join s_d_organization org on r.obj_id = org.id where r.obj_type = 2 and org.level_id = (select max(level_id)-1 from s_d_organization) and u.id = #{user_id}
and org.parent_id = #{parent_id}
AND org.freeze_status=0 limit 1

select u.id user_id ,u.user_code ,u.user_real_name_en ,u.user_real_name ,ur.role_id ,rr.rights_id ,rr.write_read ,org.id org_id ,org.organization_id org_code ,org.name_cn org_name ,org.parent_id org_parent_id ,org.level_id org_level_id from s_d_user u left join s_d_user_role ur on u.id = ur.user_id left join s_d_role_rights rr on ur.role_id = rr.role_id left join s_d_rights r on rr.rights_id = r.rights_id left join s_d_organization org on r.obj_id = org.id where r.obj_type = 2 and org.level_id = (select max(level_id)-1 from s_d_organization) and u.id = #{user_id}
and org.parent_id = #{parent_id}
AND org.freeze_status=0

mapper.insert:
	INSERT INTO s_d_organization(organization_id
									, name_cn
									, parent_id
									, level_id
									, level_name
									, freeze_status
									, create_person
									, create_date
									, create_time)
  	VALUES(
  		#{organization_id}
  		,#{name_cn}
  		,#{parent_id}
  		,#{level_id}
  		,#{level_name}
  		,#{freeze_status}
  		,#{create_person}
  		,CURRENT_DATE
  		,CURRENT_TIMESTAMP
  	)

mapper.update:
UPDATE s_d_organization SET freeze_status = #{freeze_status}
WHERE id in
#{item}
